INSERT INTO founders (id, company_id, name, role, period, status, ownership, background, key_contributions, current_activity) VALUES
('galitsky', 'magnit', 'Сергей Галицкий (Арутюнян)', 'Основатель, главный идеолог и гендиректор сети до 2018 года', '1994–02.2018', 'inactive', 'До 2006: >70% через «Тандер»; 2006 (после IPO): ~58%; Февраль 2018: продал ВТБ 29,1%, остался ~3%', 'Родился в 1967 году в Сочи (Лазаревское). Окончил экономфак КубГУ. В конце 80-х — начале 90-х работал в коммерческом банке в Краснодаре. 1993–1994: создаёт бизнес по дистрибуции косметики и бытовой химии (компании «ТрансАзия», затем «Тандер»). 1998: открывает первый магазин «Магнит» в Краснодаре.', '["Агрессивное масштабирование «магазинов у дома» в регионах","Построение вертикально интегрированной модели: собственные логистические центры, транспорт, сельхозпроизводство","IPO 2006 года, вывод компании в топ-250 мировых ритейлеров по версии Deloitte"]', 'Президент и владелец ФК «Краснодар». Развивает винодельню и бренд «Галицкий и Галицкий». В управлении Магнита не участвует.'),
INSERT INTO founders (id, company_id, name, role, period, status, ownership, background, key_contributions, current_activity) VALUES
('rogachev', 'pyaterochka', 'Андрей Владимирович Рогачёв', 'Ключевой основатель, один из крупнейших акционеров до 2011 года', '1999–2011', 'inactive', '2011: продал последний пакет 11,14% за ~$1,38 млрд', 'Российский предприниматель, долларовый миллиардер по данным Forbes. Первый магазин «Пятёрочка» открылся в Петербурге 15 февраля 1999 года.', '["Создание формата продуктового дискаунтера в посткризисной России","Быстрое масштабирование: от 4 магазинов в 1999 до 751 в 2005 году","IPO Pyaterochka Holding N.V. на LSE в 2005 году","Слияние с «Перекрёстком» и создание X5 Retail Group в 2006 году"]', 'С 2012 года развивает собственную сеть дискаунтеров «Верный». В управлении X5/«Пятёрочкой» не участвует.'),
INSERT INTO founders (id, company_id, name, role, period, status, ownership, background, key_contributions, current_activity) VALUES
